import React, { useState, useEffect } from 'react';
import './Services.css';
import { useLanguage } from '../languageContext/laguageContext';
import { translations } from '../../translations';
import ServiceCard from '../ServiceCard/ServiceCard';
import SkeletonCard from '../Skeleton/SkeletonCard';
import ConsultModal from '../ConsultModal/ConsultModal';

type ServiceItem = {
  title: string;
  iconSrc: string;
  points?: string[];
};

const Services: React.FC = () => {
  const { language } = useLanguage();
  const t = translations[language].services;

  const [loading, setLoading] = useState(true);
  const [modalOpen, setModalOpen] = useState(false);

  useEffect(() => {
    const timer = setTimeout(() => setLoading(false), 500);
    return () => clearTimeout(timer);
  }, []);

  const items = t.items as ServiceItem[];

  return (
    <section id="services" className="services" aria-labelledby="services-title">
      <h2 id="services-title" className="services__title">
        {t.title}
      </h2>

      <div className="services__underline" aria-hidden="true" />

      <div className="services__grid">
        {loading
          ? Array.from({ length: 4 }).map((_, i) => <SkeletonCard key={i} />)
          : items.map((service) => (
            <ServiceCard
              key={service.title}
              title={service.title}
              iconSrc={service.iconSrc}
              points={service.points}
              onClick={() => setModalOpen(true)}
            />
          ))}
      </div>

      <ConsultModal isOpen={modalOpen} onClose={() => setModalOpen(false)}>
        <h3>Obține o consultație acum</h3>
        <p>Sunați-ne la:</p>
        <p style={{ fontWeight: 'bold', fontSize: '18px', margin: '10px 0' }}>
          +373 123 456 789
        </p>
        <p style={{ fontWeight: 'bold', fontSize: '18px' }}>+373 987 654 321</p>
      </ConsultModal>
    </section>
  );
};

export default Services;
