import React, { useEffect, useRef, useState } from 'react';
import './GalleryWork.css';
import { useLanguage } from '../languageContext/laguageContext';
import { translations } from '../../translations';
import { gallery } from './gallery';

type Slide = {
  src: string;
  alt?: string;
  caption?: string;
};

const GalleryWork: React.FC = () => {
  const { language } = useLanguage();
  const t = translations[language].gallery;
  const [index, setIndex] = useState(0);
  const timerRef = useRef<number | null>(null);
  const slides: Slide[] = gallery;

  // autoplay blând
  useEffect(() => {
    timerRef.current = window.setInterval(() => {
      setIndex((p) => (p + 1) % slides.length);
    }, 5000);
    return () => {
      if (timerRef.current) window.clearInterval(timerRef.current);
    };
  }, [slides.length]);

  const goto = (i: number) => setIndex((i + slides.length) % slides.length);
  const prev = () => goto(index - 1);
  const next = () => goto(index + 1);

  // stop autoplay la hover/focus (UX mai bun)
  const pause = () => timerRef.current && window.clearInterval(timerRef.current);
  const resume = () => {
    pause();
    timerRef.current = window.setInterval(() => {
      setIndex((p) => (p + 1) % slides.length);
    }, 5000);
  };

  if (!slides.length) return null;

  return (
    <section id="work" className="gw" aria-labelledby="gw-title">
      <div className="gw__container">
        {/* Stânga – text */}
        <div className="gw__text">
          <h2 id="gw-title" className="gw__title">{t.title}</h2>
          <div className="gw__underline" aria-hidden="true" />
          <p className="gw__desc">{t.description}</p>
          {t.cta && (
            <a href="#contact" className="gw__cta">{t.cta}</a>
          )}
        </div>

        {/* Dreapta – carousel */}
        <div
          className="gw__carousel"
          onMouseEnter={pause}
          onMouseLeave={resume}
          onFocus={pause}
          onBlur={resume}
          aria-roledescription="carousel"
          aria-label={t.carouselLabel}
        >
          <div className="gw__viewport">
            {slides.map((s, i) => (
              <figure
                key={i}
                className={`gw__slide ${i === index ? 'is-active' : ''}`}
                aria-hidden={i !== index}
              >
                <img
                  src={s.src}
                  alt={s.alt ?? ''}
                  loading="lazy"
                  className="gw__image"
                />
                {s.caption && <figcaption className="gw__caption">{s.caption}</figcaption>}
              </figure>
            ))}
          </div>

          {/* Controls */}
          <button
            className="gw__arrow gw__arrow--left"
            onClick={prev}
            aria-label={t.prev}
          >
            ❮
          </button>
          <button
            className="gw__arrow gw__arrow--right"
            onClick={next}
            aria-label={t.next}
          >
            ❯
          </button>

          {/* Dots */}
          <div className="gw__dots" role="tablist" aria-label={t.thumbs}>
            {slides.map((_, i) => (
              <button
                key={i}
                role="tab"
                aria-selected={i === index}
                aria-controls={`gw-slide-${i}`}
                className={`gw__dot ${i === index ? 'is-active' : ''}`}
                onClick={() => goto(i)}
              />
            ))}
          </div>
        </div>
      </div>
    </section>
  );
};

export default GalleryWork;
