import React, { useEffect, useRef, useState } from 'react';
import './GalleryCarousel.css';

export type GalleryItem = { src: string; alt?: string; caption?: string };

type Props = {
  items: GalleryItem[];
  intervalMs?: number; // default 4000
};

const GalleryCarousel: React.FC<Props> = ({ items, intervalMs = 4000 }) => {
  const [idx, setIdx] = useState(0);
  const timerRef = useRef<number | null>(null);
  const wrapperRef = useRef<HTMLDivElement | null>(null);

  // autoplay
  useEffect(() => {
    stop();
    timerRef.current = window.setInterval(() => {
      setIdx((p) => (p + 1) % items.length);
    }, intervalMs);
    return stop;
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [items.length, intervalMs]);

  const stop = () => {
    if (timerRef.current) {
      clearInterval(timerRef.current);
      timerRef.current = null;
    }
  };

  const go = (to: number) => {
    if (!items.length) return;
    const next = (to + items.length) % items.length;
    setIdx(next);
  };

  const prev = () => go(idx - 1);
  const next = () => go(idx + 1);

  // swipe (mobil)
  useEffect(() => {
    const el = wrapperRef.current;
    if (!el) return;

    let startX = 0;
    let moved = false;

    const onTouchStart = (e: TouchEvent) => {
      startX = e.touches[0].clientX;
      moved = false;
      stop();
    };
    const onTouchMove = (e: TouchEvent) => {
      const dx = e.touches[0].clientX - startX;
      moved = Math.abs(dx) > 24;
    };
    const onTouchEnd = (e: TouchEvent) => {
      const endX = e.changedTouches[0].clientX;
      const dx = endX - startX;
      if (moved) {
        if (dx > 30) prev();
        if (dx < -30) next();
      }
      // repornim autoplay
      if (!timerRef.current) {
        timerRef.current = window.setInterval(() => {
          setIdx((p) => (p + 1) % items.length);
        }, intervalMs);
      }
    };

    el.addEventListener('touchstart', onTouchStart, { passive: true });
    el.addEventListener('touchmove', onTouchMove, { passive: true });
    el.addEventListener('touchend', onTouchEnd);
    return () => {
      el.removeEventListener('touchstart', onTouchStart);
      el.removeEventListener('touchmove', onTouchMove);
      el.removeEventListener('touchend', onTouchEnd);
    };
  }, [idx, intervalMs, items.length]);

  if (!items.length) return null;

  return (
    <section className="gcarousel" aria-roledescription="carousel">
      <div
        className="gcarousel__viewport"
        ref={wrapperRef}
        onMouseEnter={stop}
        onMouseLeave={() => {
          if (!timerRef.current) {
            timerRef.current = window.setInterval(() => {
              setIdx((p) => (p + 1) % items.length);
            }, intervalMs);
          }
        }}
      >
        {items.map((it, i) => (
          <figure
            key={i}
            className={`gcarousel__slide ${i === idx ? 'is-active' : ''}`}
            aria-hidden={i === idx ? 'false' : 'true'}
          >
            <img
              src={it.src}
              alt={it.alt ?? ''}
              className="gcarousel__img"
              loading="lazy"
              draggable={false}
            />
            {it.caption && <figcaption className="gcarousel__cap">{it.caption}</figcaption>}
          </figure>
        ))}
      </div>

      <button className="gcarousel__arrow gcarousel__arrow--left" onClick={prev} aria-label="Prev">
        ❮
      </button>
      <button className="gcarousel__arrow gcarousel__arrow--right" onClick={next} aria-label="Next">
        ❯
      </button>

      <div className="gcarousel__dots" role="tablist" aria-label="Select slide">
        {items.map((_, i) => (
          <button
            key={i}
            role="tab"
            aria-selected={i === idx}
            className={`gcarousel__dot ${i === idx ? 'is-active' : ''}`}
            onClick={() => go(i)}
          />
        ))}
      </div>
    </section>
  );
};

export default GalleryCarousel;
