import React, { useEffect, useRef, useState, useCallback } from 'react';
import './GallerySection.css';
import { useLanguage } from '../languageContext/laguageContext';
import { translations } from '../../translations';

// dacă ai deja un array cu imagini, îl poți importa aici.
// presupunem că îl ai ca `gallery`:
import { gallery } from './galleryData'; // [{src,alt,caption?}, ...]

type Item = { src: string; alt: string; caption?: string };

const AUTO_MS = 4500;

const GallerySection: React.FC = () => {
  const { language } = useLanguage();
  const t = translations[language];

  const items: Item[] = gallery;
  const [index, setIndex] = useState(0);
  const intervalRef = useRef<number | null>(null);

  const next = useCallback(() => {
    setIndex((i) => (i + 1) % items.length);
  }, [items.length]);

  const prev = useCallback(() => {
    setIndex((i) => (i - 1 + items.length) % items.length);
  }, [items.length]);

  // Pornește/reporneste autoplay
  const startAuto = useCallback(() => {
    if (intervalRef.current) window.clearInterval(intervalRef.current);
    intervalRef.current = window.setInterval(next, AUTO_MS);
  }, [next]);

  const stopAuto = useCallback(() => {
    if (intervalRef.current) {
      window.clearInterval(intervalRef.current);
      intervalRef.current = null;
    }
  }, []);

  // init autoplay
  useEffect(() => {
    startAuto();
    return () => stopAuto();
  }, [startAuto, stopAuto]);

  // reset timer când user-ul interacționează
  const handleUserNext = () => {
    stopAuto();
    next();
    startAuto();
  };
  const handleUserPrev = () => {
    stopAuto();
    prev();
    startAuto();
  };
  const handleUserGo = (i: number) => {
    stopAuto();
    setIndex(i);
    startAuto();
  };

  return (
    <section id="gallery" className="gsec" aria-labelledby="gallery-title">
      <h2 id="gallery-title" className="gsec__title">
        {t.gallery?.title ?? 'Galerie lucrări'}
      </h2>

      <div className="gsec__viewport">
        {items.map((it, i) => (
          <img
            key={i}
            src={it.src}
            alt={it.alt}
            className={`gsec__img ${i === index ? 'is-active' : ''}`}
            draggable={false}
            loading="lazy"
          />
        ))}

        <button
          className="gsec__arrow gsec__arrow--left"
          aria-label="Imaginea precedentă"
          onClick={handleUserPrev}
        >
          ❮
        </button>
        <button
          className="gsec__arrow gsec__arrow--right"
          aria-label="Imaginea următoare"
          onClick={handleUserNext}
        >
          ❯
        </button>

        <div className="gsec__dots" role="tablist" aria-label="Navigator galerie">
          {items.map((_, i) => (
            <button
              key={i}
              className={`gsec__dot ${i === index ? 'is-active' : ''}`}
              aria-label={`Mergi la imaginea ${i + 1}`}
              aria-selected={i === index}
              role="tab"
              onClick={() => handleUserGo(i)}
            />
          ))}
        </div>
      </div>
    </section>
  );
};

export default GallerySection;
