import React, { useEffect, useMemo, useRef, useState } from 'react';
import './GalleryMosaic.css';
import {gallery} from './gallery';

type Item = { src: string; alt?: string };

// TODO: pune aici imaginile tale (poți avea 20–60+)
const ALL_IMAGES: Item[] = gallery
const VISIBLE_TILES = 16;        // câte plăcuțe afișăm simultan
const SWAP_EVERY_MS = 2800;      // la cât timp facem un „refresh” parțial
const SWAP_COUNT = 4;            // câte plăcuțe schimbăm la fiecare refresh

function shuffle<T>(arr: T[]): T[] {
  const a = arr.slice();
  for (let i = a.length - 1; i > 0; i--) {
    const j = Math.floor(Math.random() * (i + 1));
    [a[i], a[j]] = [a[j], a[i]];
  }
  return a;
}

const SIZE_PATTERN: Array<'s'|'m'|'l'> = [
  'l','s','m','m',
  'm','l','s','m',
  's','m','l','m',
  'm','s','m','l',
];

export default function GalleryMosaic() {
  const pool = useMemo(() => shuffle(ALL_IMAGES), []);
  const [cursor, setCursor] = useState(0);

  // inițial umplem vizibilul
  const initial = useMemo(() => {
    const out: Item[] = [];
    for (let i = 0; i < VISIBLE_TILES; i++) {
      out.push(pool[(cursor + i) % pool.length]);
    }
    return out;
  }, [pool, cursor]);

  const [visible, setVisible] = useState(initial);
  const keyCounter = useRef(0); // ca să forțăm re-animarea la swap

  // la fiecare interval, înlocuim câteva poziții cu următoarele imagini din pool
  useEffect(() => {
    const id = setInterval(() => {
      setVisible((prev) => {
        const next = prev.slice();
        let localCursor = cursor;

        // alegem SWAP_COUNT poziții distincte
        const positions = shuffle([...Array(VISIBLE_TILES).keys()]).slice(0, SWAP_COUNT);

        positions.forEach((pos) => {
          localCursor = (localCursor + 1) % pool.length;
          next[pos] = pool[localCursor];
        });

        setCursor(localCursor);
        keyCounter.current++; // schimbăm cheia de bază pentru re-animare
        return next;
      });
    }, SWAP_EVERY_MS);

    return () => clearInterval(id);
  }, [cursor, pool]);

  return (
    <section className="gmosaic" id="gallery" aria-labelledby="gmosaic-title">
      <h2 id="gmosaic-title" className="gmosaic__title">Galerie lucrări</h2>
      <div className="gmosaic__underline" aria-hidden="true" />

      <div className="gmosaic__grid" role="list">
        {visible.map((item, i) => {
          const size = SIZE_PATTERN[i % SIZE_PATTERN.length]; // alocare S/M/L
          // cheia include un counter ca să declanșeze animația de fade pe swap
          const k = `${i}-${keyCounter.current}-${item.src}`;
          return (
            <article
              key={k}
              role="listitem"
              className={`gmosaic__tile gmosaic__tile--${size}`}
            >
              <img
                className="gmosaic__img"
                src={item.src}
                alt={item.alt ?? ''}
                loading="lazy"
              />
            </article>
          );
        })}
      </div>
    </section>
  );
}
