// src/components/ServiceCard/ServiceCard.tsx
import React from 'react';
import './ServiceCard.css';

export interface ServiceCardProps {
  title: string;
  iconSrc: string;
  points?: string[];
  /** Deschide modalul de consultanță când utilizatorul apasă cardul */
  onRequestConsult?: () => void;
}

const ServiceCard: React.FC<ServiceCardProps> = ({ title, iconSrc, points, onRequestConsult }) => {
  const handleKeyDown: React.KeyboardEventHandler<HTMLDivElement> = (e) => {
    if (!onRequestConsult) return;
    if (e.key === 'Enter' || e.key === ' ') {
      e.preventDefault();
      onRequestConsult();
    }
  };

  return (
    <div
      className="service-card"
      role={onRequestConsult ? 'button' : 'article'}
      tabIndex={0}
      aria-label={title}
      onClick={onRequestConsult}
      onKeyDown={handleKeyDown}
    >
      <div className="service-card__header">
        <span
          className="service-card__icon-mask"
          style={{ ['--icon' as unknown as string]: `url(${iconSrc})` }}
          aria-hidden="true"
        />
        <h3 className="service-card__title">{title}</h3>
      </div>

      {points?.length ? (
        <ul className="service-card__list">
          {points.map((p, i) => (
            <li key={i}>{p}</li>
          ))}
        </ul>
      ) : null}
    </div>
  );
};

export default ServiceCard;
