import React, { useEffect, useMemo, useState } from 'react';
import './GalleryMosaic.css';
import { galleryExtended } from './gallery';
const ROTATE_INTERVAL = 6000; // 6 secunde

const GalleryMosaic: React.FC = () => {
  const [index, setIndex] = useState(0);

  // schimbăm imaginile la fiecare 6 secunde
  useEffect(() => {
    const id = setInterval(() => {
      setIndex((prev) => (prev + 1) % 3); // 3 seturi
    }, ROTATE_INTERVAL);
    return () => clearInterval(id);
  }, []);

  // alegem 12 imagini pe rând
  const start = index * 12;
  const items = galleryExtended.slice(start, start + 12);

  return (
    <section id="gallery" className="gallery">
      <h2 className="gallery__title">Galerie lucrări</h2>
      <div className="mosaic">
        {items.map((img, i) => (
          <div
            key={i}
            className={`tile tile-${(i % 7) + 1}`} // diferite dimensiuni
            style={{ backgroundImage: `url(${img.src})` }}
          >
            <div className="overlay">
              <span>{img.caption}</span>
            </div>
          </div>
        ))}
      </div>
    </section>
  );
};

export default GalleryMosaic;
