import React from 'react';
import './ChiptuningBlock.css';
import { powerImg, dynamicsImg, fuelImg, smoothImg, errorImg, safetyImg } from 'images/chiptuning';
// dacă vrei imagini dedicate pentru stage, importă-le aici:
// import { stage1Img, stage2Img, stage3Img } from 'images/chiptuning';

const features = [
  {
    title: 'Creștere putere',
    text: ['Până la +10% la motoare atmosferice', 'Până la +50% la motoare turbo'],
    img: powerImg,
  },
  {
    title: 'Dinamica îmbunătățită',
    text: ['Accelerație mai rapidă și mai lină', 'Schimbări de trepte mai rare'],
    img: dynamicsImg,
  },
  {
    title: 'Economie de combustibil',
    text: ['Reducere consum cu până la 15%', 'Optimizare costuri de exploatare'],
    img: fuelImg,
  },
  {
    title: 'Funcționare lină',
    text: ['Schimbare trepte lină pentru cutii automate', 'Control îmbunătățit al mașinii'],
    img: smoothImg,
  },
  {
    title: 'Corectare erori',
    text: ['Rezolvarea erorilor software din fabrică', 'Optimizarea parametrilor motorului'],
    img: errorImg,
  },
  {
    title: 'Siguranță și fiabilitate',
    text: ['Fără intervenții mecanice majore', 'Păstrarea echilibrului motorului'],
    img: safetyImg,
  },
];

const stages = [
  { title: 'Stage 1', desc: 'Optimizare software de bază pentru putere și economie.' },
  { title: 'Stage 2', desc: 'Chiptuning avansat cu mici modificări hardware.' },
  { title: 'Stage 3', desc: 'Maxim de performanță – necesită modificări extinse.' },
];

export default function Chiptuning() {
  return (
    <section id="chiptuning" className="chip">
      <h2 className="chip__title">Ce oferă chiptuning-ul</h2>

      <div className="chip__grid">
        {features.map((f, i) => (
          <article key={i} className="chip__card" aria-label={f.title}>
            <div className="chip__content">
              <h3 className="chip__card-title">{f.title}</h3>
              <ul className="chip__list">
                {f.text.map((line, idx) => (
                  <li key={idx}>{line}</li>
                ))}
              </ul>
            </div>

            <div className="chip__media">
              <img src={f.img} alt="" aria-hidden="true" loading="lazy" />
            </div>
          </article>
        ))}
      </div>

      {/* bloc stages */}
      <div className="chip__stages">
        {stages.map((s, i) => (
          <div key={i} className="chip__stage">
            {/* aici poți pune imagine: <img src={stage1Img} alt={s.title}/> */}
            <div className="chip__stage-circle">{s.title}</div>
            <p>{s.desc}</p>
          </div>
        ))}
      </div>
    </section>
  );
}
