// src/components/GalleryGrid/GalleryGrid.tsx
import React, { useState } from 'react';
import './GalleryGrid.css';
import { useLanguage } from '../languageContext/laguageContext';
import { translations } from '../../translations';
import { gallery } from './gallery';

const GalleryGrid: React.FC = () => {
  const { language } = useLanguage();
  const t = translations[language].gallery;
  const [selected, setSelected] = useState<string | null>(null);

  return (
    <section id="work" className="ggrid" aria-labelledby="ggrid-title">
      <h2 id="ggrid-title" className="ggrid__title">{t.title}</h2>
      <div className="ggrid__underline" />

      <p className="ggrid__desc">{t.description}</p>

      <div className="ggrid__grid">
        {gallery.map((img, i) => (
          <button
            key={i}
            className="ggrid__item"
            onClick={() => setSelected(img.src)}
            aria-label={img.alt ?? `Imagine ${i + 1}`}
          >
            <img src={img.src} alt={img.alt ?? ''} loading="lazy" />
          </button>
        ))}
      </div>

      {/* Lightbox */}
      {selected && (
        <div className="ggrid__lightbox" onClick={() => setSelected(null)}>
          <img src={selected} alt="" />
        </div>
      )}
    </section>
  );
};

export default GalleryGrid;
