// src/components/ConsultModal/ConsultModal.tsx
import React from 'react';
import './ConsultModal.css';

interface ConsultModalProps {
  open: boolean;
  serviceTitle?: string;
  onClose: () => void;
}

const ConsultModal: React.FC<ConsultModalProps> = ({ open, serviceTitle, onClose }) => {
  if (!open) return null;

  const onBackdrop = (e: React.MouseEvent<HTMLDivElement>) => {
    if (e.target === e.currentTarget) onClose();
  };

  return (
    <div className="cm-backdrop" onClick={onBackdrop} role="presentation">
      <div
        className="cm-dialog"
        role="dialog"
        aria-modal="true"
        aria-labelledby="cm-title"
      >
        <button className="cm-close" aria-label="Close" onClick={onClose}>×</button>

        <h3 id="cm-title" className="cm-title">Obține o consultație acum</h3>
        {serviceTitle && <p className="cm-subtitle">Serviciu: <strong>{serviceTitle}</strong></p>}

        <form
          className="cm-form"
          onSubmit={(e) => {
            e.preventDefault();
            onClose();
            // aici poți adăuga trimitere către backend / email etc.
          }}
        >
          <label className="cm-field">
            <span>Nume</span>
            <input type="text" name="name" required />
          </label>

          <label className="cm-field">
            <span>Telefon</span>
            <input type="tel" name="phone" required />
          </label>

          <label className="cm-field">
            <span>Mesaj</span>
            <textarea name="message" rows={3} />
          </label>

          <button className="cm-submit" type="submit">Trimite</button>
        </form>
      </div>
    </div>
  );
};

export default ConsultModal;
