// src/components/GalleryGrid/GalleryGrid.tsx
import React, { useState } from 'react';
import './GalleryGrid.css';
import { useLanguage } from '../languageContext/laguageContext';
import { translations } from '../../translations';
import { gallery } from './gallery';

const GalleryGrid: React.FC = () => {
  const [selected, setSelected] = useState<number | null>(null);

  const closeLightbox = () => setSelected(null);

  const showPrev = (e: React.MouseEvent) => {
    e.stopPropagation();
    if (selected === null) return;
    setSelected((selected - 1 + gallery.length) % gallery.length);
  };

  const showNext = (e: React.MouseEvent) => {
    e.stopPropagation();
    if (selected === null) return;
    setSelected((selected + 1) % gallery.length);
  };

  return (
    <section id="gallery" className="ggrid">
      <h2 className="ggrid__title">Galerie lucrări</h2>
      <div className="ggrid__underline" aria-hidden="true" />

      <div className="ggrid__grid">
        {gallery.map((img, i) => (
          <img
            key={i}
            src={img.src}
            alt={img.alt ?? ''}
            loading="lazy"
            className="ggrid__img"
            onClick={() => setSelected(i)}
          />
        ))}
      </div>

      {selected !== null && (
        <div className="ggrid__lightbox" onClick={closeLightbox}>
          <button className="ggrid__nav ggrid__prev" onClick={showPrev}>‹</button>
          <img
            src={gallery[selected].src}
            alt={gallery[selected].alt ?? ''}
            className="ggrid__lightbox-img"
          />
          <button className="ggrid__nav ggrid__next" onClick={showNext}>›</button>
          <button className="ggrid__close" onClick={closeLightbox}>✖</button>
        </div>
      )}
    </section>
  );
};

export default GalleryGrid;
