import React from 'react';
import './Chiptuning.css';
import powerImg from 'images/chiptuning/engine.png';


const features = [
  {
    title: 'Creștere putere',
    text: [
      'Până la +10% la motoare atmosferice',
      'Până la +50% la motoare turbo',
    ],
    img: powerImg,
  },
  {
    title: 'Dinamica îmbunătățită',
    text: [
      'Accelerație mai rapidă și mai lină',
      'Schimbări de trepte mai rare',
    ],
    img: dynamicsImg,
  },
  {
    title: 'Economie de combustibil',
    text: [
      'Reducere consum cu până la 15%',
      'Optimizare costuri de exploatare',
    ],
    img: fuelImg,
  },
  {
    title: 'Funcționare lină',
    text: [
      'Schimbare trepte lină pentru cutii automate',
      'Control îmbunătățit al mașinii',
    ],
    img: smoothImg,
  },
  {
    title: 'Corectare erori',
    text: [
      'Rezolvarea erorilor software din fabrică',
      'Optimizarea parametrilor motorului',
    ],
    img: errorImg,
  },
  {
    title: 'Siguranță și fiabilitate',
    text: [
      'Fără intervenții mecanice majore',
      'Păstrarea echilibrului motorului',
    ],
    img: safetyImg,
  },
];

export default function Chiptuning() {
  return (
    <section className="chip">
      <h2 className="chip__title">Ce oferă chiptuning-ul</h2>
      <div className="chip__grid">
        {features.map((f, i) => (
          <div key={i} className="chip__card">
            <img src={f.img} alt={f.title} className="chip__icon" />
            <h3>{f.title}</h3>
            <ul>
              {f.text.map((line, idx) => (
                <li key={idx}>{line}</li>
              ))}
            </ul>
          </div>
        ))}
      </div>
    </section>
  );
}
